using System.Collections; using System.Collections.Generic; using UnityEngine;  public class RopeDestroy : MonoBehaviour {  	public GameObject ballon; 	//public int DestroyRopeTime=10; 	public int timeToDestroyRope=10; 	int timerCounterClock=0; 	// Use this for initialization 	void Start () {  		//int timeToDestroyRope=10; 	}  	// Update is called once per frame 	void Update () {   		DestroyBallon (); 	}      	void DestroyBallon() 	{ 		if(ballon==null) 		{ 			StartTimerAndDestroy (timeToDestroyRope);  		} 	}    	void StartTimerAndDestroy(int destroyTime) 	{ 		timerCounterClock++;  		if(timerCounterClock == destroyTime) 		{ 			Object.Destroy (this.gameObject); 		}  	} }  